    borderBottomColor: '#F2F2F7'
  },
  settingRowHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: spacing.xs
  },
  settingRowTitleLeft: {
    flexDirection: 'row',
    alignItems: 'center'
  },
  settingRowTitle: {
    fontSize: 16,
    fontWeight: '600'
  },
  editIconBox: {
    marginLeft: spacing.xs,
    borderRadius: 12,
    padding: spacing.xs,
    backgroundColor: '#F2F2F7'
  },
