    fontWeight: '600'
  },
  settingRowDesc: {
    flexDirection: 'row',
    gap: spacing.md,
    marginLeft: 32
  },
  settingRowDescText: {
    fontSize: 13,
    color: COLORS.textSub
  },
  addRow: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: spacing.md,
    paddingVertical: spacing.sm,
    borderTopWidth: 1,
    borderTopColor: '#F2F2F7'
  },
  addRowLabel: {
    marginLeft: spacing.sm,
    fontSize: 15,
    color: COLORS.primary,
    fontWeight: '500'
  },
  editRow: {
    flexDirection: 'row',
    marginTop: spacing.sm,
    marginBottom: spacing.sm,
    gap: spacing.sm
  },
  editActionRow: {
    flexDirection: 'row',
    justifyContent: 'flex-end',
    gap: spacing.sm,
    marginTop: spacing.sm
  },
  geoHeaderRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: spacing.sm
  },
  geoMapMock: {
    height: 160,
    borderRadius: radius.lg,
    backgroundColor: '#E5E5EA',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: spacing.md
  },
  geoMapCircle: {
    width: 120,
    height: 120,
    borderRadius: 60,
    borderWidth: 2,
    borderColor: COLORS.primary,
    backgroundColor: 'rgba(0,122,255,0.12)'
  },
  geoRadiusRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: spacing.sm
  },
  geoRadiusValue: {
    fontSize: 14,
    fontWeight: '700',
    color: COLORS.primary
  },
  geoControlRow: {
    flexDirection: 'row',
    gap: spacing.sm,
    marginBottom: spacing.md
  },
  geoDisabledText: {
    fontSize: 13,
    color: COLORS.textSub,
    textAlign: 'center'
  },
  searchRow: {
    flexDirection: 'row',
    alignItems: 'center',
    borderRadius: radius.md,
    borderWidth: 1,
    borderColor: COLORS.cardBorder,
    backgroundColor: 'rgba(255,255,255,0.9)',
    paddingHorizontal: spacing.sm,
    paddingVertical: spacing.xs,
    marginTop: spacing.sm
  },
  searchInput: {
    flex: 1,
    fontSize: 14,
    marginLeft: spacing.xs
  },
  subgroupSectionLabel: {
    fontSize: 12,
    color: COLORS.textSub,
    marginBottom: spacing.xs
  },
  subgroupRow: {
    flexDirection: 'row',
    alignItems: 'center'
  },
  subgroupName: {
    fontSize: 15,
    fontWeight: '600'
  },
  subgroupRadius: {
    fontSize: 12,
    color: COLORS.textSub
  },
  saveBottomRow: {
    marginTop: spacing.lg,
    paddingBottom: spacing.lg
  },
  groupRow: {
    flexDirection: 'row',
    alignItems: 'center'
  },
  groupIconBox: {
    width: 40,
    height: 40,
    borderRadius: 20,
    backgroundColor: 'rgba(88,86,214,0.1)',
    alignItems: 'center',
    justifyContent: 'center',
    marginRight: spacing.md
  },
  groupName: {
    fontSize: 16,
    fontWeight: '600'
  },
  groupCode: {
    fontSize: 12,
    color: COLORS.textSub
  }
});

sourcesContent:
import React, { useEffect, useRef, useState } from 'react';
import type { ReactNode } from 'react';
import {
  Alert,
  Modal,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  View,
  Pressable,
  Animated,
} from 'react-native';
import {
  Menu,
  Clock,
  List,
  LayoutDashboard,
  Settings,
  X,
  Building2,
  Users,
  MapPin,
  FileText,
  Layers,
  ChevronDown,
  ChevronRight,
  ChevronLeft,
  Bell,
  Shield,
  LogOut,
  Plus,
  MinusCircle,
  Edit2,
  LogIn,
  Map,
  Search,
  Save,
  Smartphone,
  CreditCard,
  User as UserIcon,
  Check,
  Globe,
  Download,
  Coffee,
  PlayCircle,
} from 'lucide-react-native';
import {
  LiquidContainer,
  LiquidCard,
  LiquidButton,
  LiquidSwitch,
  LiquidSegmentControl,
  LiquidSheet,
  LiquidDock,
  LiquidTheme,
} from './liquid-ui/liquid-ui_RN';

/**
 * ======================================================
 * 型定義
 * ======================================================
 */

type ViewMode = '日' | '週' | '月' | 'カスタム';
type TabKey =
  | 'punch'
  | 'history'
  | 'settings'
  | 'settings_profile'
  | 'admin'
  | 'admin_org_settings'
  | 'admin_group_settings'
  | 'admin_subgroup_settings'
  | 'admin_group_edit'
  | 'admin_subgroup_edit'
  | 'reports';

type PunchTypeId = 'in' | 'break_start' | 'break_end' | 'out';
type PunchStatus = 'none' | 'working' | 'break' | 'finished';

type User = {
  id: string;
  name: string;
  email: string;
  role: string;
  avatar: string | null;
  currentGroup: string;
  currentSubgroup: string;
};

type Group = { id: number; name: string; code: string };
type Subgroup = { id: number; name: string; radius: number };
type HistoryEvent = {
  id: number;
  type: string;
  time: string;
  valid: boolean;
  punchType: PunchTypeId;
};
type WeeklyStat = { day: string; hours: number; date: string };
type MonthlyStat = { date: string; day: string; hours: number; status: string };

type PunchSettingItem = {
  id: number;
  title: string;
  type: 'system' | 'break' | 'custom';
  required: boolean;
  enabled: boolean;
  labelIn: string;
  labelOut: string;
};

type IconComponent = React.ComponentType<{
  size?: number;
  color?: string;
}>;

/**
 * ======================================================
 * 定数・デザイントークン
 * ======================================================
 */

const APP_CONFIG = {
  appName: 'timetime',
  companyName: '株式会社 timetime建設',
  version: 'v1.0.0',
  geofence: {
    defaultRadius: 200,
    minRadius: 50,
    maxRadius: 500,
    step: 10,
    displayScale: 0.7,
  },
};

const ROLE_LABELS = {
  admin: '組織管理者',
};

const APP_COPY = {
  version: `${APP_CONFIG.appName} ${APP_CONFIG.version}`,
};

const COLORS = {
  primary: '#007AFF',
  success: '#34C759',
  danger: '#FF3B30',
  warning: '#FF9500',
  indigo: '#5856D6',
  textMain: '#1C1C1E',
  textSub: '#8E8E93',
  bgTop: '#E0EAFC',
  bgBottom: '#CFDEF3',
  cardBg: 'rgba(255,255,255,0.9)',
  cardBorder: 'rgba(255,255,255,0.8)',
  separator: 'rgba(60,60,67,0.2)',
};

const spacing = {
  xs: 4,
  sm: 8,
  md: 16,
  lg: 24,
  xl: 32,
};

const radius = {
  sm: 8,
  md: 12,
  lg: 16,
  xl: 24,
  pill: 999,
};

const layout = {
  headerHeight: 56,
  dockHeight: 72,
};

const MOTION = {
  punchPressScale: 0.95,
  punchSpring: {
    friction: 7,
    tension: 140,
  },
  punchRelease: {
    friction: 6,
    tension: 120,
  },
};

const VIEW_MODE_OPTIONS: ViewMode[] = ['日', '週', '月'];
const REPORT_VIEW_MODES: ViewMode[] = ['日', '週', '月', 'カスタム'];

const ADMIN_TABS: { id: TabKey; label: string }[] = [
  { id: 'punch', label: '打刻' },
  { id: 'history', label: '履歴' },
  { id: 'admin', label: '管理' },
];

const GENERAL_TABS: { id: TabKey; label: string }[] = [
  { id: 'punch', label: '打刻' },
  { id: 'history', label: '履歴' },
];

const TAB_ICON_MAP: Record<TabKey, IconComponent> = {
  punch: Clock,
  history: List,
  admin: LayoutDashboard,
  settings: Settings,
  settings_profile: Settings,
  admin_org_settings: Globe,
  admin_group_settings: Layers,
  admin_subgroup_settings: Map,
  admin_group_edit: Edit2,
  admin_subgroup_edit: Edit2,
  reports: FileText,
};

const PUNCH_TYPE_META: Record<
  PunchTypeId,
  { id: PunchTypeId; label: string; color: string; icon: IconComponent }
> = {
  in: { id: 'in', label: '出勤', color: '#007AFF', icon: LogIn },
  break_start: { id: 'break_start', label: '休憩', color: '#FF9500', icon: Coffee },
  break_end: { id: 'break_end', label: '再開', color: '#007AFF', icon: PlayCircle },
  out: { id: 'out', label: '退勤', color: '#34C759', icon: LogOut },
};

const PUNCH_TYPE_ORDER: PunchTypeId[] = ['in', 'break_start', 'break_end', 'out'];

const PUNCH_STATUS_TRANSITIONS: Record<PunchTypeId, PunchStatus> = {
  in: 'working',
  break_start: 'break',
  break_end: 'working',
  out: 'finished',
};

const DEFAULT_SELECTION_BY_STATUS: Record<PunchStatus, PunchTypeId> = {
  none: 'in',
  working: 'out',
  break: 'break_end',
  finished: 'in',
};

const PUNCH_PROCESSING_DELAY_MS = 800;
const NOW_TICK_INTERVAL_MS = 1000;

/**
 * ======================================================
 * ユーティリティ
 * ======================================================
 */

const getWeekRange = (date: Date) => {
  const dayOfWeek = date.getDay();
  const diffToMon = date.getDate() - dayOfWeek + (dayOfWeek === 0 ? -6 : 1);
  const monday = new Date(date);
  monday.setDate(diffToMon);
  const sunday = new Date(monday);
  sunday.setDate(monday.getDate() + 6);
  return { monday, sunday };
};

const formatDate = (date: Date, mode: ViewMode) => {
  const y = date.getFullYear();
  const m = date.getMonth() + 1;
  const d = date.getDate();
  const day = ['日', '月', '火', '水', '木', '金', '土'][date.getDay()];

  if (mode === '日') return `${y}年${m}月${d}日(${day})`;
  if (mode === '月') return `${y}年${m}月`;
  if (mode === '週') {
    const { monday, sunday } = getWeekRange(date);
    return `${monday.getMonth() + 1}/${monday.getDate()} - ${
      sunday.getMonth() + 1
    }/${sunday.getDate()}`;
  }
  if (mode === 'カスタム') return 'カスタム期間';
  return '';
};

const shiftDateByViewMode = (baseDate: Date, mode: ViewMode, direction: number) => {
  const next = new Date(baseDate);
  if (mode === '日') next.setDate(next.getDate() + direction);
  else if (mode === '週') next.setDate(next.getDate() + direction * 7);
  else if (mode === '月') next.setMonth(next.getMonth() + direction);
  return next;
};

const getWeekDate = (baseDate: Date, offset: number) => {
  const { monday } = getWeekRange(baseDate);
  const target = new Date(monday);
  target.setDate(monday.getDate() + offset);
  return target;
};

const getShortDate = (date: Date) =>
  `${date.getMonth() + 1}/${date.getDate()} (${['日', '月', '火', '水', '木', '金', '土'][date.getDay()]})`;

const formatDateInputValue = (date: Date) => date.toISOString().slice(0, 10);

const getPunchColor = (type: PunchTypeId | null) =>
  type ? PUNCH_TYPE_META[type]?.color ?? '#ddd' : '#ddd';

const getPunchLabel = (type: PunchTypeId | null) =>
  type ? PUNCH_TYPE_META[type]?.label ?? '選択' : '選択';

const useNow = () => {
  const [now, setNow] = useState(new Date());
  useEffect(() => {
    const timerId = setInterval(() => setNow(new Date()), NOW_TICK_INTERVAL_MS);
    return () => clearInterval(timerId);
  }, []);
  return now;
};

/**
 * ======================================================
 * モックデータ
 * ======================================================
 */

const MOCK_USER_DATA: User = {
  id: '8829-1123-9991',
  name: '田中 太郎',
  email: 'taro.tanaka@timetime.co.jp',
  role: ROLE_LABELS.admin,
  avatar: null,
  currentGroup: '関東エリア',
  currentSubgroup: '新宿駅前現場',
};

const MOCK_GROUPS: Group[] = [
  { id: 1, name: '関東エリア', code: 'GRP-101' },
  { id: 2, name: '関西エリア', code: 'GRP-102' },
  { id: 3, name: '九州エリア', code: 'GRP-103' },
];

const MOCK_SUBGROUPS: Record<string, Subgroup[]> = {
  関東エリア: [
    { id: 101, name: '新宿駅前現場', radius: 150 },
    { id: 102, name: '渋谷開発PJ', radius: 200 },
  ],
  関西エリア: [{ id: 201, name: '梅田タワー', radius: 100 }],
  九州エリア: [{ id: 301, name: '博多駅前', radius: 120 }],
};

const INITIAL_PUNCH_ITEMS: PunchSettingItem[] = [
  {
    id: 1,
    title: '出勤・退勤',
    type: 'system',
    required: true,
    enabled: true,
    labelIn: '出勤',
    labelOut: '退勤',
  },
  {
    id: 2,
    title: '休憩',
    type: 'break',
    required: false,
    enabled: true,
    labelIn: '休憩',
    labelOut: '再開',
  },
];

const getHistoryEvents = (baseDate: Date): HistoryEvent[] => {
  const seed = baseDate.getDate() + baseDate.getMonth();
  return [
    { id: 1, type: '出勤', punchType: 'in', time: `08:${30 + (seed % 30)}`, valid: true },
    { id: 2, type: '休憩開始', punchType: 'break_start', time: '12:00', valid: true },
    { id: 3, type: '休憩終了', punchType: 'break_end', time: '13:00', valid: true },
    { id: 4, type: '退勤', punchType: 'out', time: `18:${10 + (seed % 40)}`, valid: true },
  ];
};

const getWeeklyStats = (baseDate: Date): WeeklyStat[] => {
  const { monday } = getWeekRange(baseDate);
  return Array.from({ length: 7 }, (_, i) => {
    const d = new Date(monday);
    d.setDate(monday.getDate() + i);
    return {
      day: ['月', '火', '水', '木', '金', '土', '日'][i],
      hours: i < 5 ? 8 + ((d.getDate() % 5) / 10) : 0,
      date: `${d.getMonth() + 1}/${d.getDate()}`,
    };
  });
};

const getMonthlyStats = (baseDate: Date): MonthlyStat[] => {
  const y = baseDate.getFullYear();
  const m = baseDate.getMonth();
  const daysInMonth = new Date(y, m + 1, 0).getDate();

  return Array.from({ length: daysInMonth }, (_, i) => {
    const d = new Date(y, m, i + 1);
    const dayOfWeek = d.getDay();
    const isWeekend = dayOfWeek === 0 || dayOfWeek === 6;
    return {
      date: `${m + 1}/${i + 1}`,
      day: ['日', '月', '火', '水', '木', '金', '土'][dayOfWeek],
      hours: isWeekend ? 0 : 8.0 + ((i % 3) * 0.25),
      status: isWeekend ? '休日' : '出勤',
    };
  });
};

const getSubgroupsForGroup = (groupName: string): Subgroup[] => {
  return MOCK_SUBGROUPS[groupName] ?? [];
};

const getDefaultSubgroupName = (groupName: string, fallback: string) => {
  const subgroups = getSubgroupsForGroup(groupName);
  return subgroups[0]?.name ?? fallback;
};

/**
 * ======================================================
 * 共通 UI コンポーネント（RN ベース）
 * ======================================================
 */

type GlassCardProps = {
  children: ReactNode;
  onPress?: () => void;
  style?: any;
  padding?: number;
};

const GlassCard: React.FC<GlassCardProps> = ({ children, onPress, style, padding }) => {
  return (
    <LiquidCard
      onPress={onPress}
      padding={padding ?? spacing.md}
      style={style}
      transparent={false}
    >
      {children}
    </LiquidCard>
  );
};

type ButtonVariant = 'primary' | 'danger' | 'ghost' | 'glass';
type ButtonSize = 'md' | 'sm' | 'icon';

type AppButtonProps = {
  children?: ReactNode;
  icon?: IconComponent;
  variant?: ButtonVariant;
  size?: ButtonSize;
  onPress?: () => void;
  style?: any;
  disabled?: boolean;
};

const AppButton: React.FC<AppButtonProps> = ({
  children,
  icon: Icon,
  variant = 'primary',
  size = 'md',
  onPress,
  style,
  disabled,
}) => {
  const iconColor =
    variant === 'primary'
      ? '#fff'
      : variant === 'danger'
      ? COLORS.danger
      : COLORS.textSub;
  const iconElement = Icon ? (
    <Icon
      size={size === 'sm' ? 16 : size === 'icon' ? 18 : 20}
      color={iconColor}
    />
  ) : undefined;
  const sizeMap: Record<ButtonSize, 'sm' | 'md' | 'lg' | 'icon'> = {
    sm: 'sm',
    md: 'md',
    icon: 'icon',
  };

  return (
    <LiquidButton
      onPress={onPress}
      variant={variant}
      size={sizeMap[size] ?? 'md'}
      icon={iconElement}
      disabled={disabled}
      style={style}
    >
      {children}
    </LiquidButton>
  );
};

type SwitchProps = {
  isOn: boolean;
  onToggle: () => void;
};

const AppSwitch: React.FC<SwitchProps> = ({ isOn, onToggle }) => {
  return (
    <Pressable onPress={onToggle} style={[styles.switchContainer, isOn && styles.switchOn]}>
      <View style={[styles.switchThumb, isOn && styles.switchThumbOn]} />
    </Pressable>
  );
};

type SegmentOption = string | { value: string; label: string };

type SegmentControlProps = {
  options: SegmentOption[];
  selected: string | ViewMode;
  onSelect: (value: string) => void;
};

const SegmentControl: React.FC<SegmentControlProps> = ({ options, selected, onSelect }) => {
  const getValue = (o: SegmentOption) => (typeof o === 'string' ? o : o.value);
  const getLabel = (o: SegmentOption) => (typeof o === 'string' ? o : o.label);

  return (
    <View style={styles.segmentRoot}>
      {options.map((o) => {
        const value = getValue(o);
        const label = getLabel(o);
        const active = value === selected;
        return (
          <Pressable
            key={value}
            onPress={() => onSelect(value)}
            style={[styles.segmentItem, active && styles.segmentItemActive]}
          >
            <Text style={[styles.segmentLabel, active && styles.segmentLabelActive]}>
              {label}
            </Text>
          </Pressable>
        );
      })}
    </View>
  );
};

type TextFieldProps = {
  label?: string;
  icon?: IconComponent;
  placeholder?: string;
  value?: string;
  onChange?: (v: string) => void;
  secureTextEntry?: boolean;
  keyboardType?: 'default' | 'numeric' | 'email-address';
  style?: any;
};

const TextField: React.FC<TextFieldProps> = ({
  label,
  icon: Icon,
  placeholder,
  value,
  onChange,
  secureTextEntry,
  keyboardType,
  style,
}) => {
  return (
    <View style={[styles.fieldContainer, style]}>
      {label && <Text style={styles.fieldLabel}>{label}</Text>}
      <View style={styles.fieldInner}>
        {Icon && <Icon size={18} color={COLORS.textSub} />}
        <TextInput
          value={value}
          onChangeText={onChange}
          placeholder={placeholder}
          secureTextEntry={secureTextEntry}
          keyboardType={keyboardType}
          style={styles.fieldInput}
        />
      </View>
    </View>
  );
};

type SelectOption = { value: string; label: string };

type SelectFieldProps = {
  label?: string;
  icon?: IconComponent;
  options: SelectOption[];
  value: string;
  onChange?: (value: string) => void;
  placeholder?: string;
  style?: any;
};

const SelectField: React.FC<SelectFieldProps> = ({
  label,
  icon: Icon,
  options,
  value,
  onChange,
  placeholder,
  style,
}) => {
  const [open, setOpen] = useState(false);
  const selected = options.find((o) => o.value === value);
  const text = selected?.label ?? placeholder ?? '選択してください';

  return (
    <View style={[styles.fieldContainer, style]}>
      {label && <Text style={styles.fieldLabel}>{label}</Text>}
      <Pressable
        onPress={() => setOpen((o) => !o)}
        style={({ pressed }) => [
          styles.selectField,
          pressed && styles.selectFieldPressed,
        ]}
      >
        {Icon && <Icon size={18} color={COLORS.textSub} />}
        <Text
          style={
            selected ? styles.selectValueText : styles.selectPlaceholderText
          }
        >
          {text}
        </Text>
        <ChevronDown size={16} color={COLORS.textSub} />
      </Pressable>
      {open && (
        <View style={styles.selectDropdown}>
          {options.map((opt) => {
            const isActive = opt.value === value;
            return (
              <Pressable
                key={opt.value}
                onPress={() => {
                  onChange?.(opt.value);
                  setOpen(false);
                }}
                style={({ pressed }) => [
                  styles.selectItem,
                  (pressed || isActive) && styles.selectItemActive,
                ]}
              >
                <Text style={styles.selectItemLabel}>{opt.label}</Text>
                {isActive && <Check size={16} color={COLORS.primary} />}
              </Pressable>
            );
          })}
        </View>
      )}
    </View>
  );
};

type DateNavigatorProps = {
  label: string;
  onPrev: () => void;
  onNext: () => void;
  style?: any;
};

const DateNavigator: React.FC<DateNavigatorProps> = ({
  label,
  onPrev,
  onNext,
  style,
}) => (
  <View style={[styles.dateNavRoot, style]}>
    <AppButton variant="ghost" size="icon" onPress={onPrev}>
      <ChevronLeft size={22} color={COLORS.textMain} />
    </AppButton>
    <Text style={styles.dateNavLabel}>{label}</Text>
    <AppButton variant="ghost" size="icon" onPress={onNext}>
      <ChevronRight size={22} color={COLORS.textMain} />
    </AppButton>
  </View>
);

type TimelineItemProps = { event: HistoryEvent; isLast: boolean };

const TimelineItem: React.FC<TimelineItemProps> = ({ event, isLast }) => {
  const meta = PUNCH_TYPE_META[event.punchType];
  const Icon = meta.icon;
  return (
    <View style={styles.timelineRow}>
      <View style={styles.timelineIconColumn}>
        <View style={[styles.timelineDot, { backgroundColor: meta.color }]}>
          <Icon size={14} color="#fff" />
        </View>
        {!isLast && <View style={styles.timelineLine} />}
      </View>
