/**
 * ======================================================
 * メイン App コンポーネント
 * ======================================================
 */

export default function App() {
  const [activeTab, setActiveTab] = useState<TabKey>('punch');
  const [menuVisible, setMenuVisible] = useState(false);
  const [sheetUserId, setSheetUserId] = useState<number | null>(null);
  const [user, setUser] = useState<User>(MOCK_USER_DATA);

  const onGroupChange = (name: string) => {
    const newSub = getDefaultSubgroupName(name, user.currentSubgroup);
    setUser((prev) => ({
      ...prev,
      currentGroup: name,
      currentSubgroup: newSub,
    }));
  };

  const onSubgroupChange = (name: string) => {
    setUser((prev) => ({ ...prev, currentSubgroup: name }));
  };

  const renderScreen = () => {
    switch (activeTab) {
      case 'punch':
        return <PunchScreen user={user} />;
      case 'history':
        return <HistoryScreen />;
      case 'settings':
        return <SettingsScreen onNavigate={setActiveTab} user={user} />;
      case 'settings_profile':
        return (
          <UserProfileEditScreen
            user={user}
            onBack={() => setActiveTab('settings')}
          />
        );
      case 'admin':
        return (
          <AdminDashboardScreen
            onMemberClick={(id) => setSheetUserId(id)}
          />
        );
      case 'admin_org_settings':
        return <AdminOrgSettingsScreen onBack={() => setActiveTab('punch')} />;
      case 'admin_group_settings':
        return (
          <AdminGroupSettingsScreen
            onBack={() => setActiveTab('punch')}
            onNavigate={setActiveTab}
          />
        );
      case 'admin_subgroup_settings':
        return (
          <AdminSubgroupSettingsScreen
            onBack={() => setActiveTab('punch')}
            onNavigate={setActiveTab}
          />
        );
      case 'admin_group_edit':
        return (
          <View style={styles.screenContainer}>
            <SettingsHeader
              title="グループ編集"
              onBack={() => setActiveTab('admin_group_settings')}
            />
            <CommonSettingsForm title="関東エリア" />
            <PunchSettingsSection />
            <GeofenceSection />
            <View style={styles.saveBottomRow}>
              <AppButton style={{ width: '100%', height: 48 }}>保存</AppButton>
            </View>
          </View>
        );
      case 'admin_subgroup_edit':
        return (
          <View style={styles.screenContainer}>
            <SettingsHeader
              title="小グループ編集"
              onBack={() => setActiveTab('admin_subgroup_settings')}
            />
            <CommonSettingsForm title="新宿駅前現場" />
            <PunchSettingsSection />
            <GeofenceSection />
            <View style={styles.saveBottomRow}>
              <AppButton style={{ width: '100%', height: 48 }}>保存</AppButton>
            </View>
          </View>
        );
      case 'reports':
        return (
          <ReportScreen
            onBack={() => setActiveTab('admin')}
            user={user}
          />
        );
      default:
        return <PunchScreen user={user} />;
    }
  };

  return (
    <SafeAreaView style={styles.appRoot}>
      <View style={styles.appBackground} />
      <View style={styles.appForeground}>
        {/* ヘッダー */}
        <View style={styles.header}>
          <AppButton
            size="icon"
            variant="glass"
            onPress={() => setMenuVisible(true)}
          >
            <Menu size={24} color={COLORS.textMain} />
          </AppButton>
        </View>

        {/* コンテンツ */}
        <ScrollView
          contentContainerStyle={styles.scrollContent}
          showsVerticalScrollIndicator={false}
        >
          {renderScreen()}
        </ScrollView>

        {/* Dock */}
        <Dock
          activeTab={activeTab}
          onTabChange={setActiveTab}
          role={user.role}
        />

        {/* サイドメニュー */}
        <SideMenu
          visible={menuVisible}
          onClose={() => setMenuVisible(false)}
          user={user}
          onNavigate={setActiveTab}
          onGroupSelect={onGroupChange}
          onSubgroupSelect={onSubgroupChange}
        />

        {/* 勤務履歴詳細シート（管理ダッシュボードのメンバー） */}
        <BottomSheet
          visible={sheetUserId != null}
          onClose={() => setSheetUserId(null)}
          title="勤務履歴詳細"
        >
          <HistoryScreen isModal />
        </BottomSheet>
      </View>
    </SafeAreaView>
  );
}

