    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: spacing.md
  },
  geoMapCircle: {
    width: 120,
    height: 120,
    borderRadius: 60,
    borderWidth: 2,
    borderColor: COLORS.primary,
    backgroundColor: 'rgba(0,122,255,0.12)'
  },
  geoRadiusRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: spacing.sm
  },
  geoRadiusValue: {
    fontSize: 14,
    fontWeight: '700',
    color: COLORS.primary
  },
  geoControlRow: {
    flexDirection: 'row',
    gap: spacing.sm,
    marginBottom: spacing.md
  },
  geoDisabledText: {
    fontSize: 13,
    color: COLORS.textSub,
    textAlign: 'center'
  },
  searchRow: {
    flexDirection: 'row',
    alignItems: 'center',
    borderRadius: radius.md,
    borderWidth: 1,
    borderColor: COLORS.cardBorder,
    backgroundColor: 'rgba(255,255,255,0.9)',
    paddingHorizontal: spacing.sm,
    paddingVertical: spacing.xs,
    marginTop: spacing.sm
  },
  searchInput: {
    flex: 1,
    fontSize: 14,
    marginLeft: spacing.xs
  },
  subgroupSectionLabel: {
    fontSize: 12,
    color: COLORS.textSub,
    marginBottom: spacing.xs
  },
  subgroupRow: {
    flexDirection: 'row',
    alignItems: 'center'
  },
  subgroupName: {
    fontSize: 15,
    fontWeight: '600'
  },
  subgroupRadius: {
    fontSize: 12,
    color: COLORS.textSub
  },
  saveBottomRow: {
    marginTop: spacing.lg,
    paddingBottom: spacing.lg
  },
  groupRow: {
    flexDirection: 'row',
    alignItems: 'center'
  },
  groupIconBox: {
    width: 40,
    height: 40,
    borderRadius: 20,
    backgroundColor: 'rgba(88,86,214,0.1)',
    alignItems: 'center',
    justifyContent: 'center',
    marginRight: spacing.md
  },
  groupName: {
    fontSize: 16,
    fontWeight: '600'
  },
  groupCode: {
    fontSize: 12,
    color: COLORS.textSub
  }
});

sourcesContent:
import React, { useCallback, useEffect, useRef, useState } from 'react';
import type { ReactNode } from 'react';
import {
  Alert,
  Modal,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  View,
  Pressable,
  Animated,
  Easing,
  Platform,
} from 'react-native';
import {
  Menu,
  Clock,
  List,
  LayoutDashboard,
  Settings,
  X,
  Building2,
  Users,
  MapPin,
  FileText,
  Layers,
  ChevronDown,
  ChevronRight,
  ChevronLeft,
  Bell,
  Shield,
  LogIn,
  LogOut,
  Plus,
  MinusCircle,
  Edit2,
  Map,
  Search,
  Save,
  Smartphone,
  CreditCard,
  User as UserIcon,
  Check,
  Globe,
  Download,
} from 'lucide-react-native';
import {
  LiquidContainer,
  LiquidCard,
  LiquidButton,
  LiquidSwitch,
  LiquidSegmentControl,
  LiquidSheet,
  LiquidDock,
  LiquidTheme,
} from './liquid-ui/liquid-ui_RN';
import {
  APP_CONFIG,
  APP_COPY,
  ADMIN_TABS,
  COLORS,
  GENERAL_TABS,
  MOTION,
  REPORT_VIEW_MODES,
  ROLE_LABELS,
  VIEW_MODE_OPTIONS,
  layout,
  radius,
  spacing,
} from './designTokens';
import {
  DEFAULT_SELECTION_BY_STATUS,
  PUNCH_PROCESSING_DELAY_MS,
  PUNCH_STATUS_TRANSITIONS,
  PUNCH_TYPE_META,
  PUNCH_TYPE_ORDER,
  getPunchColor,
  getPunchLabel,
} from './punchMeta';
import {
  formatDate,
  formatDateInputValue,
  getShortDate,
  getWeekDate,
  shiftDateByViewMode,
} from './dateUtils';
import { useNow } from './hooks/useNow';
import {
  INITIAL_PUNCH_ITEMS,
  MOCK_GROUPS,
  MOCK_SUBGROUPS,
  MOCK_USER_DATA,
  getDefaultSubgroupName,
  getHistoryEvents,
  getMonthlyStats,
  getSubgroupsForGroup,
  getWeeklyStats,
} from '../../data/mockData';
import type {
  Group,
  HistoryEvent,
  IconComponent,
  MonthlyStat,
  PunchSettingItem,
  PunchStatus,
  PunchTypeId,
  Subgroup,
  TabKey,
  User,
  ViewMode,
  WeeklyStat,
} from './types';

/**
 * 型・定数・モックデータは周辺モジュールへ分離。
 * このファイルでは UI 実装に集中する。
 */

/**
 * ======================================================
 * 共通 UI コンポーネント（RN ベース）
 * ======================================================
 */

const TAB_ICON_MAP: Record<TabKey, IconComponent> = {
  punch: Clock,
  history: List,
  admin: LayoutDashboard,
  settings: Settings,
  settings_profile: Settings,
  admin_org_settings: Globe,
  admin_group_settings: Layers,
  admin_subgroup_settings: Map,
  admin_group_edit: Edit2,
  admin_subgroup_edit: Edit2,
  reports: FileText,
};

type GlassCardProps = {
  children: ReactNode;
  onPress?: () => void;
  style?: any;
  padding?: number;
};

const GlassCard: React.FC<GlassCardProps> = ({ children, onPress, style, padding }) => {
  return (
    <LiquidCard
      onPress={onPress}
      padding={padding ?? spacing.md}
      style={style}
      transparent={false}
    >
      {children}
    </LiquidCard>
  );
};

type ButtonVariant = 'primary' | 'danger' | 'ghost' | 'glass';
type ButtonSize = 'md' | 'sm' | 'icon';

type AppButtonProps = {
  children?: ReactNode;
  icon?: IconComponent;
  variant?: ButtonVariant;
  size?: ButtonSize;
  onPress?: () => void;
  style?: any;
  disabled?: boolean;
};

const AppButton: React.FC<AppButtonProps> = ({
  children,
  icon: Icon,
  variant = 'primary',
  size = 'md',
  onPress,
  style,
  disabled,
}) => {
  const iconColor =
    variant === 'primary'
      ? '#fff'
      : variant === 'danger'
      ? COLORS.danger
      : COLORS.textSub;
  const iconElement = Icon ? (
    <Icon
      size={size === 'sm' ? 16 : size === 'icon' ? 18 : 20}
      color={iconColor}
    />
  ) : undefined;
  const sizeMap: Record<ButtonSize, 'sm' | 'md' | 'lg' | 'icon'> = {
    sm: 'sm',
    md: 'md',
    icon: 'icon',
  };

  return (
    <LiquidButton
      onPress={onPress}
      variant={variant}
      size={sizeMap[size] ?? 'md'}
      icon={iconElement}
      disabled={disabled}
      style={style}
    >
      {children}
    </LiquidButton>
  );
};

type SwitchProps = {
  isOn: boolean;
  onToggle: () => void;
};

const AppSwitch: React.FC<SwitchProps> = ({ isOn, onToggle }) => {
  return <LiquidSwitch value={isOn} onValueChange={onToggle} />;
};

type SegmentOption = string | { value: string; label: string };

type SegmentControlProps = {
  options: SegmentOption[];
  selected: string | ViewMode;
  onSelect: (value: string) => void;
};

const SegmentControl: React.FC<SegmentControlProps> = ({ options, selected, onSelect }) => {
  const normalized = options.map((o) =>
    typeof o === 'string' ? { value: o, label: o } : { value: o.value, label: o.label },
  );

  return (
    <LiquidSegmentControl
      options={normalized}
      value={String(selected)}
      onChange={onSelect}
    />
  );
};

type TextFieldProps = {
  label?: string;
  icon?: IconComponent;
  placeholder?: string;
  value?: string;
  onChange?: (v: string) => void;
  secureTextEntry?: boolean;
  keyboardType?: 'default' | 'numeric' | 'email-address';
  style?: any;
};

const TextField: React.FC<TextFieldProps> = ({
  label,
  icon: Icon,
  placeholder,
  value,
  onChange,
  secureTextEntry,
  keyboardType,
  style,
}) => {
  return (
    <View style={[styles.fieldContainer, style]}>
      {label && <Text style={styles.fieldLabel}>{label}</Text>}
      <View style={styles.fieldInner}>
        {Icon && <Icon size={18} color={COLORS.textSub} />}
        <TextInput
          value={value}
          onChangeText={onChange}
          placeholder={placeholder}
          secureTextEntry={secureTextEntry}
          keyboardType={keyboardType}
          style={styles.fieldInput}
        />
      </View>
    </View>
  );
};

type SelectOption = { value: string; label: string };

type SelectFieldProps = {
  label?: string;
  icon?: IconComponent;
  options: SelectOption[];
  value: string;
  onChange?: (value: string) => void;
  placeholder?: string;
  style?: any;
};

const SelectField: React.FC<SelectFieldProps> = ({
  label,
  icon: Icon,
  options,
  value,
  onChange,
  placeholder,
  style,
}) => {
  const [open, setOpen] = useState(false);
  const selected = options.find((o) => o.value === value);
  const text = selected?.label ?? placeholder ?? '選択してください';

  return (
    <View style={[styles.fieldContainer, style]}>
      {label && <Text style={styles.fieldLabel}>{label}</Text>}
      <Pressable
        onPress={() => setOpen((o) => !o)}
        style={({ pressed }) => [
          styles.selectField,
          pressed && styles.selectFieldPressed,
        ]}
      >
        {Icon && <Icon size={18} color={COLORS.textSub} />}
        <Text
          style={
            selected ? styles.selectValueText : styles.selectPlaceholderText
          }
        >
          {text}
        </Text>
        <ChevronDown size={16} color={COLORS.textSub} />
      </Pressable>
      {open && (
        <View style={styles.selectDropdown}>
          {options.map((opt) => {
            const isActive = opt.value === value;
            return (
              <Pressable
                key={opt.value}
                onPress={() => {
                  onChange?.(opt.value);
                  setOpen(false);
                }}
                style={({ pressed }) => [
                  styles.selectItem,
                  (pressed || isActive) && styles.selectItemActive,
                ]}
              >
                <Text style={styles.selectItemLabel}>{opt.label}</Text>
                {isActive && <Check size={16} color={COLORS.primary} />}
              </Pressable>
            );
          })}
        </View>
      )}
    </View>
  );
};

type DateNavigatorProps = {
  label: string;
  onPrev: () => void;
  onNext: () => void;
  style?: any;
};

const DateNavigator: React.FC<DateNavigatorProps> = ({
  label,
  onPrev,
  onNext,
  style,
}) => (
  <View style={[styles.dateNavRoot, style]}>
    <AppButton variant="ghost" size="icon" onPress={onPrev}>
      <ChevronLeft size={22} color={COLORS.textMain} />
    </AppButton>
    <Text style={styles.dateNavLabel}>{label}</Text>
    <AppButton variant="ghost" size="icon" onPress={onNext}>
      <ChevronRight size={22} color={COLORS.textMain} />
    </AppButton>
  </View>
);

type TimelineItemProps = { event: HistoryEvent; isLast: boolean };

const TimelineItem: React.FC<TimelineItemProps> = ({ event, isLast }) => {
  const meta = PUNCH_TYPE_META[event.punchType];
  const Icon = meta.icon;
  return (
    <View style={styles.timelineRow}>
      <View style={styles.timelineIconColumn}>
        <View style={[styles.timelineDot, { backgroundColor: meta.color }]}>
          <Icon size={14} color="#fff" />
        </View>
        {!isLast && <View style={styles.timelineLine} />}
      </View>
      <View style={styles.timelineContent}>
        <Text style={[styles.timelineTitle, { color: meta.color }]}>
          {meta.label}
          {event.valid && <Text> ✓</Text>}
        </Text>
        <Text style={styles.timelineSub}>
          {event.punchType.includes('break') ? 'Break Time' : 'Work Time'}
        </Text>
      </View>
      <View style={styles.timelineTimeColumn}>
        <Text style={styles.timelineTime}>{event.time}</Text>
      </View>
    </View>
  );
};

type SettingsHeaderProps = {
  title: string;
  onBack?: () => void;
  action?: ReactNode;
};

const SettingsHeader: React.FC<SettingsHeaderProps> = ({
  title,
  onBack,
  action,
}) => (
  <View style={styles.settingsHeaderRoot}>
    <View style={styles.settingsHeaderLeft}>
      {onBack && (
        <AppButton
          variant="ghost"
          size="icon"
          onPress={onBack}
          style={{ marginRight: spacing.sm }}
        >
          <ChevronLeft size={22} color={COLORS.textMain} />
        </AppButton>
      )}
      <Text style={styles.settingsHeaderTitle}>{title}</Text>
    </View>
    {action}
  </View>
);

/**
 * ======================================================
 * Dock・サイドメニュー・シート
 * ======================================================
 */

type DockProps = {
  activeTab: TabKey;
  onTabChange: (t: TabKey) => void;
  role: string;
};

const Dock: React.FC<DockProps> = ({ activeTab, onTabChange, role }) => {
  const tabs = (role === ROLE_LABELS.admin ? ADMIN_TABS : GENERAL_TABS).map((t) => {
    const Icon = TAB_ICON_MAP[t.id];
    const isActive = t.id === activeTab;
    return {
      id: t.id,
      label: t.label,
      icon: (
        <Icon
          size={22}
          color={isActive ? LiquidTheme.colors.primary : COLORS.textSub}
        />
      ),
    };
  });

  return (
    <LiquidDock
      tabs={tabs}
      activeId={activeTab}
      onChange={onTabChange}
    />
  );
};

type SideMenuProps = {
  visible: boolean;
  onClose: () => void;
  user: User;
  onNavigate: (tab: TabKey) => void;
  onGroupSelect: (name: string) => void;
  onSubgroupSelect: (name: string) => void;
};

const SideMenu: React.FC<SideMenuProps> = ({
  visible,
  onClose,
  user,
  onNavigate,
  onGroupSelect,
  onSubgroupSelect,
}) => {
  const slideX = useRef(new Animated.Value(-320)).current;
  const backdropOpacity = useRef(new Animated.Value(0)).current;
  const [mounted, setMounted] = useState(visible);
  const [groupOpen, setGroupOpen] = useState(true);
  const [subgroupOpen, setSubgroupOpen] = useState(true);
  const [adminOpen, setAdminOpen] = useState(false);
  const groupAnim = useRef(new Animated.Value(groupOpen ? 1 : 0)).current;
  const subgroupAnim = useRef(new Animated.Value(subgroupOpen ? 1 : 0)).current;
  const adminAnim = useRef(new Animated.Value(adminOpen ? 1 : 0)).current;
  const [groupContentHeight, setGroupContentHeight] = useState(0);
  const [subgroupContentHeight, setSubgroupContentHeight] = useState(0);
  const [adminContentHeight, setAdminContentHeight] = useState(0);

  useEffect(() => {
    if (visible) {
      setMounted(true);
      Animated.parallel([
        Animated.timing(slideX, {
          toValue: 0,
          duration: 240,
          easing: Easing.out(Easing.cubic),
          useNativeDriver: false,
        }),
        Animated.timing(backdropOpacity, {
          toValue: 1,
          duration: 200,
          easing: Easing.out(Easing.cubic),
          useNativeDriver: false,
        }),
      ]).start();
    } else {
      Animated.parallel([
        Animated.timing(slideX, {
          toValue: -320,
          duration: 220,
          easing: Easing.out(Easing.cubic),
          useNativeDriver: false,
        }),
        Animated.timing(backdropOpacity, {
          toValue: 0,
          duration: 160,
          easing: Easing.out(Easing.cubic),
          useNativeDriver: false,
        }),
      ]).start(({ finished }) => {
        if (finished) setMounted(false);
      });
    }
  }, [visible, slideX, backdropOpacity]);

  const animateAccordion = useCallback(
    (anim: Animated.Value, open: boolean) => {
      Animated.spring(anim, {
        toValue: open ? 1 : 0,
        useNativeDriver: false,
        bounciness: 10,
        speed: 14,
      }).start();
    },
    [],
  );

  useEffect(() => {
    animateAccordion(groupAnim, groupOpen);
  }, [groupOpen, animateAccordion, groupAnim]);

  useEffect(() => {
    animateAccordion(subgroupAnim, subgroupOpen);
  }, [subgroupOpen, animateAccordion, subgroupAnim]);

  useEffect(() => {
    animateAccordion(adminAnim, adminOpen);
  }, [adminOpen, animateAccordion, adminAnim]);

  if (!mounted) return null;

  const handleReport = () => {
    onNavigate('reports');
    onClose();
  };

  return (
    <Modal visible transparent animationType="none" statusBarTranslucent>
      <Animated.View
        style={[
          styles.menuOverlay,
          { opacity: backdropOpacity },
        ]}
      >
        <Pressable style={StyleSheet.absoluteFill} onPress={onClose} />
      </Animated.View>
      <Animated.View
        style={[
          styles.menuPanel,
          {
            transform: [{ translateX: slideX }],
          },
        ]}
      >
        <View style={styles.menuHeader}>
          <View style={styles.menuUserRow}>
            <View style={styles.menuAvatar}>
              <Text style={styles.menuAvatarText}>
                {user.name.charAt(0)}
              </Text>
            </View>
            <View>
              <Text style={styles.menuUserName}>{user.name}</Text>
              <Text style={styles.menuUserRole}>{user.role}</Text>
            </View>
          </View>
          <Pressable onPress={onClose} style={styles.menuCloseButton}>
            <X size={18} color={COLORS.textSub} />
          </Pressable>
        </View>

        <ScrollView contentContainerStyle={styles.menuScroll}>
          <Text style={styles.menuSectionTitle}>所属</Text>
          <GlassCard padding={spacing.md} style={styles.menuOrgCard}>
            <Building2 size={18} color={COLORS.primary} />
            <Text style={styles.menuOrgText}>{APP_CONFIG.companyName}</Text>
          </GlassCard>

          {/* グループ選択 */}
          <View style={styles.menuAccordionBlock}>
            <View
              style={[
                styles.menuAccordionSurface,
                {
                  backgroundColor: 'rgba(88,86,214,0.08)',
                  borderColor: 'rgba(88,86,214,0.2)',
                },
              ]}
            >
              <Pressable
                onPress={() => setGroupOpen((o) => !o)}
                style={styles.menuAccordionHeader}
              >
                <Users size={18} color={COLORS.indigo} />
                <Text style={styles.menuAccordionTitle}>{user.currentGroup}</Text>
                <Animated.View
                  style={[
                    styles.menuAccordionArrow,
                    {
                      transform: [
                        {
                          rotate: groupAnim.interpolate({
                            inputRange: [0, 1],
                            outputRange: ['0deg', '180deg'],
                          }),
                        },
                      ],
                      backgroundColor: groupOpen
                        ? 'rgba(88,86,214,0.12)'
                        : 'rgba(0,0,0,0.04)',
                    },
                  ]}
                >
                  <ChevronDown size={16} color={COLORS.textSub} />
                </Animated.View>
              </Pressable>
              <Animated.View
                pointerEvents={groupOpen ? 'auto' : 'none'}
                style={[
                  styles.menuAccordionContentWrapper,
                  {
                    height: groupAnim.interpolate({
                      inputRange: [0, 1],
                      outputRange: [0, Math.max(groupContentHeight, 1)],
                    }),
                    opacity: groupAnim,
                  },
                ]}
              >
                <View
                  style={styles.menuAccordionContent}
                  onLayout={(e) =>
                    setGroupContentHeight(e.nativeEvent.layout.height)
                  }
                >
                  {MOCK_GROUPS.map((g) => {
                    const active = g.name === user.currentGroup;
                    return (
                      <Pressable
                        key={g.id}
                        onPress={() => onGroupSelect(g.name)}
                        style={styles.menuListRow}
                      >
                        <Text
                          style={[
                            styles.menuListLabel,
                            active && { color: COLORS.primary },
                          ]}
                        >
                          {g.name}
                        </Text>
                        {active && <Check size={14} color={COLORS.primary} />}
                      </Pressable>
                    );
                  })}
                </View>
              </Animated.View>
            </View>
          </View>

          {/* 小グループ */}
          <View style={styles.menuAccordionBlock}>
            <View
              style={[
                styles.menuAccordionSurface,
                {
                  backgroundColor: 'rgba(52,199,89,0.08)',
                  borderColor: 'rgba(52,199,89,0.2)',
                },
              ]}
            >
              <Pressable
                onPress={() => setSubgroupOpen((o) => !o)}
                style={styles.menuAccordionHeader}
              >
                <MapPin size={18} color={COLORS.success} />
                <Text style={styles.menuAccordionTitle}>
                  {user.currentSubgroup}
                </Text>
                <Animated.View
                  style={[
                    styles.menuAccordionArrow,
                    {
                      transform: [
                        {
                          rotate: subgroupAnim.interpolate({
                            inputRange: [0, 1],
                            outputRange: ['0deg', '180deg'],
                          }),
                        },
                      ],
                      backgroundColor: subgroupOpen
                        ? 'rgba(52,199,89,0.14)'
                        : 'rgba(0,0,0,0.04)',
                    },
                  ]}
                >
                  <ChevronDown size={16} color={COLORS.textSub} />
                </Animated.View>
              </Pressable>
              <Animated.View
                pointerEvents={subgroupOpen ? 'auto' : 'none'}
                style={[
                  styles.menuAccordionContentWrapper,
                  {
                    height: subgroupAnim.interpolate({
                      inputRange: [0, 1],
                      outputRange: [0, Math.max(subgroupContentHeight, 1)],
                    }),
                    opacity: subgroupAnim,
                  },
                ]}
              >
                <View
                  style={styles.menuAccordionContent}
                  onLayout={(e) =>
                    setSubgroupContentHeight(e.nativeEvent.layout.height)
                  }
                >
                  {(MOCK_SUBGROUPS[user.currentGroup] ?? []).map((s) => {
                    const active = s.name === user.currentSubgroup;
                    return (
                      <Pressable
                        key={s.id}
                        onPress={() => {
                          onSubgroupSelect(s.name);
                          onClose();
                        }}
                        style={styles.menuListRow}
                      >
                        <Text
                          style={[
                            styles.menuListLabel,
                            active && { color: COLORS.primary },
                          ]}
                        >
                          {s.name}
                        </Text>
                        {active && <Check size={14} color={COLORS.primary} />}
                      </Pressable>
                    );
                  })}
                </View>
              </Animated.View>
            </View>
          </View>

          {/* 管理 */}
          {user.role === ROLE_LABELS.admin && (
            <>
              <Text style={[styles.menuSectionTitle, { marginTop: spacing.lg }]}>
                組織管理
              </Text>
              <GlassCard
                padding={spacing.md}
                style={styles.menuReportsCard}
                onPress={handleReport}
              >
                <FileText size={18} color={COLORS.warning} />
                <View style={{ flex: 1, marginLeft: spacing.sm }}>
                  <Text style={styles.menuReportsTitle}>レポート</Text>
                  <Text style={styles.menuReportsSub}>勤怠出力・分析</Text>
                </View>
              </GlassCard>

              <View style={styles.menuAccordionBlock}>
                <View
                  style={[
                    styles.menuAccordionSurface,
                    {
                      backgroundColor: 'rgba(28,28,30,0.05)',
                      borderColor: 'rgba(28,28,30,0.15)',
                    },
                  ]}
                >
                  <Pressable
                    onPress={() => setAdminOpen((o) => !o)}
                    style={styles.menuAccordionHeader}
                  >
                    <Building2 size={18} color={COLORS.textSub} />
                    <Text style={styles.menuAccordionTitle}>組織設定</Text>
                    <Animated.View
                      style={[
                        styles.menuAccordionArrow,
                        {
                          transform: [
                            {
                              rotate: adminAnim.interpolate({
                                inputRange: [0, 1],
                                outputRange: ['0deg', '180deg'],
                              }),
                            },
                          ],
                          backgroundColor: adminOpen
