type PunchScreenProps = { user: User };

const PunchScreen: React.FC<PunchScreenProps> = ({ user }) => {
  const [status, setStatus] = useState<PunchStatus>('working');
  const [selection, setSelection] = useState<PunchTypeId>(
    DEFAULT_SELECTION_BY_STATUS.working,
  );
  const [processing, setProcessing] = useState(false);
  const now = useNow();
  const timerRef = useRef<ReturnType<typeof setTimeout> | null>(null);

  useEffect(() => {
    return () => {
      if (timerRef.current) clearTimeout(timerRef.current);
    };
  }, []);

  useEffect(() => {
    setSelection(DEFAULT_SELECTION_BY_STATUS[status]);
  }, [status]);

  const handlePunch = () => {
    if (!selection) {
      setSelection(DEFAULT_SELECTION_BY_STATUS[status]);
      return;
    }
    setProcessing(true);
    if (timerRef.current) clearTimeout(timerRef.current);
    timerRef.current = setTimeout(() => {
      setProcessing(false);
      const nextStatus = PUNCH_STATUS_TRANSITIONS[selection] ?? status;
      setStatus(nextStatus);
      setSelection(DEFAULT_SELECTION_BY_STATUS[nextStatus]);
    }, PUNCH_PROCESSING_DELAY_MS);
  };

  const color = getPunchColor(selection);

  return (
    <View style={styles.screenContainer}>
      {/* 現場名 */}
      <View style={styles.punchLocationChip}>
        <View
          style={[
            styles.statusDot,
            status === 'working' && { backgroundColor: COLORS.success },
            status === 'break' && { backgroundColor: COLORS.warning },
            status === 'finished' && { backgroundColor: COLORS.textSub },
          ]}
        />
        <Text style={styles.punchLocationText}>{user.currentSubgroup}</Text>
      </View>

      {/* 時刻 */}
      <View style={styles.punchTimeBlock}>
        <Text style={styles.punchTimeText}>
          {now.toLocaleTimeString('ja-JP', {
            hour: '2-digit',
            minute: '2-digit',
          })}
        </Text>
        <Text style={styles.punchDateText}>
          {now.toLocaleDateString('ja-JP', {
            month: 'long',
            day: 'numeric',
            weekday: 'short',
          })}
        </Text>
      </View>

      {/* 種別選択 */}
      <View style={styles.punchTypeGrid}>
        {PUNCH_TYPE_ORDER.map((id) => {
          const meta = PUNCH_TYPE_META[id];
          const active = selection === id;
          const Icon = meta.icon;
          return (
            <Pressable
              key={id}
              onPress={() => setSelection(id)}
              style={[
                styles.punchTypeItem,
                active && styles.punchTypeItemActive,
              ]}
            >
              <View
                style={[
                  styles.punchTypeIconCircle,
                  active && { backgroundColor: meta.color },
                ]}
              >
                <Icon size={18} color={active ? '#fff' : meta.color} />
              </View>
              <Text
                style={[
                  styles.punchTypeLabel,
                  active && styles.punchTypeLabelActive,
                ]}
              >
                {meta.label}
              </Text>
            </Pressable>
          );
        })}
      </View>

      {/* メイン打刻ボタン */}
      <View style={styles.punchMainButtonWrapper}>
        <Pressable
          onPress={handlePunch}
          style={({ pressed }) => [
            styles.punchMainButton,
            { backgroundColor: color },
            (pressed || processing) && styles.punchMainButtonPressed,
          ]}
        >
          <Text style={styles.punchMainCaption}>TAP TO</Text>
          <Text style={styles.punchMainLabel}>
            {processing ? '...' : getPunchLabel(selection)}
          </Text>
        </Pressable>
      </View>

      {/* サマリーカード */}
      <View style={styles.punchSummaryRow}>
        <GlassCard padding={spacing.md} style={styles.punchSummaryCard}>
          <Text style={styles.summaryTitle}>今日の合計</Text>
          <Text style={styles.summaryValue}>1.0h</Text>
        </GlassCard>
        <GlassCard padding={spacing.md} style={styles.punchSummaryCard}>
          <Text style={styles.summaryTitle}>現在の状態</Text>
          <Text
            style={[
              styles.summaryStatusText,
              status === 'working' && { color: COLORS.primary },
              status === 'break' && { color: COLORS.warning },
              status === 'finished' && { color: COLORS.textSub },
            ]}
          >
            {status === 'working'
              ? '勤務中'
              : status === 'break'
              ? '休憩中'
              : '退勤済'}
          </Text>
        </GlassCard>
      </View>
    </View>
  );
};

type HistoryScreenProps = {
  isModal?: boolean;
};

const HistoryScreen: React.FC<HistoryScreenProps> = ({ isModal }) => {
  const [mode, setMode] = useState<ViewMode>('日');
  const [date, setDate] = useState(new Date());

  const events = getHistoryEvents(date);
  const weekly = getWeeklyStats(date);
  const monthly = getMonthlyStats(date);

  const handleShift = (dir: number) => {
    setDate((prev) => shiftDateByViewMode(prev, mode, dir));
  };

  const content = (
    <View style={styles.screenContainer}>
      {!isModal && (
        <Text style={styles.screenTitle}>勤務履歴</Text>
      )}

      <View style={{ marginBottom: spacing.md }}>
        <SegmentControl
          options={VIEW_MODE_OPTIONS}
          selected={mode}
          onSelect={(v) => setMode(v as ViewMode)}
        />
      </View>

      <DateNavigator
        label={formatDate(date, mode)}
        onPrev={() => handleShift(-1)}
        onNext={() => handleShift(1)}
      />

      {mode === '日' && (
        <GlassCard padding={spacing.md}>
          <View style={styles.dailyHeaderRow}>
            <Text style={styles.dailyHeaderDate}>{getShortDate(date)}</Text>
            <View style={styles.dailyHeaderRight}>
              <Clock size={14} color={COLORS.textSub} />
              <Text style={styles.dailyHeaderTotal}>合計: 8.25h</Text>
            </View>
          </View>
          <View style={{ marginTop: spacing.md }}>
            {events.map((ev, i) => (
              <TimelineItem
                key={ev.id}
                event={ev}
                isLast={i === events.length - 1}
              />
            ))}
          </View>
        </GlassCard>
      )}

      {mode === '週' && (
        <GlassCard padding={spacing.md}>
          <View style={styles.weekChartRow}>
            {weekly.map((w, idx) => {
              const d = getWeekDate(date, idx);
              const h = (w.hours / 10) * 120;
              return (
                <Pressable
                  key={w.day}
                  onPress={() => {
                    setDate(d);
                    setMode('日');
                  }}
                  style={styles.weekChartBarWrapper}
                >
                  <View
                    style={[
                      styles.weekChartBar,
                      { height: h || 4 },
                      w.hours === 0 && { backgroundColor: '#E5E5EA' },
                    ]}
                  />
                  <Text style={styles.weekChartDay}>{w.day}</Text>
                  <Text style={styles.weekChartDate}>{w.date}</Text>
                </Pressable>
              );
            })}
          </View>
          <View style={styles.weekTotalRow}>
            <Text style={styles.weekTotalLabel}>週合計</Text>
            <Text style={styles.weekTotalValue}>41.25h</Text>
          </View>
        </GlassCard>
      )}

      {mode === '月' && (
        <GlassCard padding={0}>
          <View style={styles.monthHeaderRow}>
            <Text style={styles.monthHeaderCellLeft}>日付</Text>
            <Text style={styles.monthHeaderCellCenter}>勤務時間</Text>
            <Text style={styles.monthHeaderCellRight}>状態</Text>
          </View>
          <ScrollView
            style={{ maxHeight: 360 }}
            contentContainerStyle={{ paddingBottom: spacing.sm }}
          >
            {monthly.map((m, index) => (
              <Pressable
                key={m.date}
                onPress={() => {
                  const target = new Date(
                    date.getFullYear(),
                    date.getMonth(),
                    index + 1,
                  );
                  setDate(target);
                  setMode('日');
                }}
                style={styles.monthRow}
              >
                <Text style={styles.monthCellLeft}>
                  {m.date}{' '}
                  <Text style={styles.monthDay}>({m.day})</Text>
                </Text>
                <Text
                  style={[
                    styles.monthCellCenter,
                    m.hours === 0 && { color: COLORS.textSub },
                  ]}
                >
                  {m.hours.toFixed(1)}h
                </Text>
                <Text
                  style={[
                    styles.monthCellRight,
                    m.status === '出勤' && { color: COLORS.success },
                  ]}
                >
                  {m.status}
                </Text>
              </Pressable>
            ))}
          </ScrollView>
          <View style={styles.monthFooterRow}>
            <Text style={styles.monthFooterLabel}>月間合計</Text>
            <Text style={styles.monthFooterValue}>124.5h</Text>
          </View>
        </GlassCard>
      )}
    </View>
  );

  if (isModal) {
    return <View>{content}</View>;
  }
  return content;
};

type ReportScreenProps = {
  onBack: () => void;
  user: User;
};

const ReportScreen: React.FC<ReportScreenProps> = ({ onBack, user }) => {
  const [targetGroup, setTargetGroup] = useState<string>(user.currentGroup);
  const [targetSubgroup, setTargetSubgroup] = useState<string>(
    user.currentSubgroup,
  );
  const [mode, setMode] = useState<ViewMode>('日');
  const [currentDate, setCurrentDate] = useState(new Date());
  const [startDateStr, setStartDateStr] = useState(formatDateInputValue(new Date()));
  const [endDateStr, setEndDateStr] = useState(formatDateInputValue(new Date()));

  const shift = (dir: number) => {
    setCurrentDate((prev) => shiftDateByViewMode(prev, mode, dir));
  };

  useEffect(() => {
    if (targetGroup === 'all') return;
    setTargetSubgroup((prev) => getDefaultSubgroupName(targetGroup, prev));
  }, [targetGroup]);

  const groupOptions: SelectOption[] = [
    { value: 'all', label: `${APP_CONFIG.companyName} (全組織)` },
    ...MOCK_GROUPS.map((g) => ({ value: g.name, label: g.name })),
  ];

  const subOptions: SelectOption[] =
    targetGroup === 'all'
      ? []
      : [
          {
            value: 'all_group',
            label: `全${targetGroup}グループ`,
          },
          ...(MOCK_SUBGROUPS[targetGroup] ?? []).map((s) => ({
            value: s.name,
            label: s.name,
          })),
        ];

  const handleExport = (fmt: 'CSV' | 'PDF') => {
    const range =
      mode === 'カスタム'
        ? `${startDateStr} - ${endDateStr}`
        : formatDate(currentDate, mode);
    Alert.alert(
      `${fmt} レポートを生成しました`,
      `期間: ${range}\n対象: ${targetGroup} / ${targetSubgroup}`,
    );
  };

  return (
    <View style={styles.screenContainer}>
      <SettingsHeader title="レポート出力" onBack={onBack} />

      <Text style={styles.sectionSubtitle}>対象者と期間の指定</Text>
      <GlassCard padding={spacing.md} style={{ marginBottom: spacing.lg }}>
        <Text style={styles.fieldSectionTitle}>対象組織 / グループ</Text>
        <SelectField
          icon={Building2}
          value={targetGroup}
          onChange={setTargetGroup}
          options={groupOptions}
          placeholder="組織を選択"
        />
        {targetGroup !== 'all' && (
          <SelectField
            icon={MapPin}
            value={targetSubgroup}
            onChange={setTargetSubgroup}
            options={subOptions}
            placeholder="現場 / 小グループを選択"
          />
        )}

        <Text style={[styles.fieldSectionTitle, { marginTop: spacing.md }]}>
          期間指定
        </Text>
        <SegmentControl
          options={REPORT_VIEW_MODES}
          selected={mode}
          onSelect={(v) => setMode(v as ViewMode)}
        />
        {mode !== 'カスタム' && (
          <DateNavigator
            label={formatDate(currentDate, mode)}
            onPrev={() => shift(-1)}
            onNext={() => shift(1)}
            style={{ marginTop: spacing.md }}
          />
        )}
        {mode === 'カスタム' && (
          <View style={styles.customRangeRow}>
            <View style={{ flex: 1 }}>
              <Text style={styles.fieldLabel}>開始日</Text>
              <TextInput
                value={startDateStr}
                onChangeText={setStartDateStr}
                style={styles.rangeInput}
              />
            </View>
            <Text style={styles.rangeSeparator}>〜</Text>
            <View style={{ flex: 1 }}>
              <Text style={styles.fieldLabel}>終了日</Text>
              <TextInput
                value={endDateStr}
                onChangeText={setEndDateStr}
                style={styles.rangeInput}
              />
            </View>
          </View>
        )}
      </GlassCard>

      <Text style={styles.sectionSubtitle}>出力フォーマット</Text>
      <View style={styles.exportRow}>
        <AppButton
          variant="primary"
          icon={Download}
          onPress={() => handleExport('CSV')}
          style={{ flex: 1, height: 48 }}
        >
          CSVエクスポート
        </AppButton>
        <AppButton
          variant="glass"
          icon={FileText}
          onPress={() => handleExport('PDF')}
          style={{ flex: 1, height: 48 }}
        >
          PDFエクスポート
        </AppButton>
      </View>
    </View>
  );
};

